    def analyze_data(self):
        coin1 = self.lineEdit_31.text()
        coin2 = self.lineEdit_32.text()
        timeframe = self.comboBox_30.currentText()
        interval = self.comboBox_6.currentText()

        if self.lineEdit_9.text():
            window = int(self.lineEdit_9.text())
        else:
            window = ''

        entry_z_score = float(self.lineEdit_5.text())
        stop_loss = float(self.lineEdit_7.text())

        print(timeframe)
        dfs = self.zaur.get_data2([coin1, coin2], timeframe, interval)

        df2 = pd.DataFrame(index=dfs[0].index, data={dfs[0].name: dfs[0].iloc[:, 3], dfs[1].name: dfs[1].iloc[:, 3]})
        analysis_df, results = self.zaur.analyze_data(df2, entry_z_score=entry_z_score, stop_loss=stop_loss, window=window)

        tab = AnalysisTabs(self.tabWidget_8, self.zaur, coin1.split('_')[0], coin2.split('_')[0], dfs, analysis_df, results, interval)
        self.tabWidget_8.addTab(tab, f'{coin1}_vs_{coin2}')
        self.tabs.setCurrentIndex(1)
        self.tabWidget_8.setCurrentIndex(-1)


    def update_base_currency(self):
        for label in [self.label_13, self.label_27, self.label_38]:
            label.setText(self.comboBox_5.currentText())


    def set_default_timeframe(self):
        self.comboBox_30.setCurrentText('3M')



    def send_to_algos(self):
        pair = f'{self.zaur.front.current_ticker}_{self.zaur.front.current_exchange.id}'
        if self.lineEdit_31.text():
            self.lineEdit_32.setText(pair)
        else:
            self.lineEdit_31.setText(pair)

    def update_intervals(self):
        self.comboBox_6.clear()
        try:
            self.comboBox_6.addItems(self.zaur.current_exchange.timeframes.keys())
        except Exception:
            pass

    def clear_pairs(self):
        self.lineEdit_31.clear()
        self.lineEdit_32.clear()

    def clear_statarb_config(self):
        for line in self.statarb_lines:
            line.clear()

    def clear_momentum_config(self):
        for line in self.momentum_lines:
            line.clear()

    def clear_custom_config(self):
        for line in self.custom_lines:
            line.clear()

