class AnalysisTabs(QtWidgets.QTabWidget):
    def __init__(self, parent, zaur, coin1, coin2, dfs, analysis_df, results, interval):
        super().__init__(parent)
        self.zaur = zaur
        self.parenttabs = parent
        self.df1 = dfs[0].copy()
        self.df2 = dfs[1].copy()

        self.results = results

        self.results['interval'] = interval

        self.coin1 = coin1
        self.coin2 = coin2

        self.analysis_df = analysis_df.copy()


        self.setGeometry(QtCore.QRect(0, 10, 901, 500))
        self.setStyleSheet("font: 75 11pt \"FreeMono Bold\";\n"
                            "background-color: rgb(20, 20, 40);\n"
                            "color: rgb(255, 255, 255);")

        self.ohlc1 = OHLCChart(self.df1, self)
        self.ohlc2 = OHLCChart(self.df2, self)
        self.spread_chart = LineChart(self.analysis_df['spread'], self)

        self.z_score_chart = LineChart(self.analysis_df.loc[10:, 'z_score'], self)
        cols2 = [col for col in self.analysis_df.columns if '100' in col]
        self.compare_chart = MultiLineChart(self.analysis_df[cols2], self)

        self.config = {'adf_reg': 'c', 'kpss_reg': 'c', 'adf_autolag': 'AIC', 'kpss_autolag': 'auto'}
        self.options = {'adf_reg': {'const': 'c', 'const+trend': 'ct', 'const+linear+quadratic trend': 'ctt', 'no const no trend': 'nc'},
                        'adf_lag': {'AIC': 'AIC', 'BIC': 'BIC', 't-stat': 't-stat'},
                        'kpss_reg': {'const': 'c', 'const+trend': 'ct'},
                        'kpss_lag': {'Hobijn': 'auto', 'Schwert': 'legacy'}}


        self.test_tab = QtWidgets.QWidget(self)
        self.test_tab.setGeometry(QtCore.QRect(0, 0, 901, 470))

        self.backtest_tab = QtWidgets.QWidget(self)
        self.backtest_tab.setGeometry(QtCore.QRect(0, 0, 901, 470))

        self.results_tab = QtWidgets.QWidget(self)
        self.results_tab.setGeometry(QtCore.QRect(0, 0, 901, 470))
        self.results_frame = QtWidgets.QFrame(self.results_tab)
        self.results_frame.setGeometry(QtCore.QRect(5, 5, 800, 460))

        self.pushButton_40 = QtWidgets.QPushButton(self)
        self.pushButton_40.setGeometry(QtCore.QRect(810, 0, 71, 31))
        self.pushButton_40.setObjectName("pushButton_40")
        self.pushButton_40.setText("close")

        self.button2 = QtWidgets.QPushButton(self.results_frame)
        self.button2.setGeometry(QtCore.QRect(480, 31, 150, 60))
        self.button2.setText('save result')

        self.groupBox = QtWidgets.QGroupBox(self.test_tab)
        self.groupBox.setGeometry(QtCore.QRect(0, 15, 901, 455))
        self.groupBox.setTitle('ADF:  regression   lags method                  KPSS:  regression   lags method')


        i = 0
        self.combos = dict()
        for k, v in self.options.items():
            combo = QtWidgets.QComboBox(self.groupBox)
            combo.addItems(v.keys())
            self.combos[k] = combo
            combo.setCurrentText(self.config[k])
            i += 1

        self.combos['adf_reg'].setGeometry(QtCore.QRect(45, 25, 80, 27))
        self.combos['adf_lag'].setGeometry(QtCore.QRect(130, 25, 80, 27))
        self.combos['kpss_reg'].setGeometry(QtCore.QRect(375, 25, 80, 27))
        self.combos['kpss_lag'].setGeometry(QtCore.QRect(470, 25, 80, 27))

        self.button = QtWidgets.QPushButton(self.groupBox)
        self.button.setGeometry(QtCore.QRect(800, 25, 70, 27))
        self.button.setText('rerun')

        self.addTab(self.ohlc1, self.coin1.split('_')[0])
        self.addTab(self.ohlc2, self.coin2.split('_')[0])
        self.addTab(self.spread_chart, "spread")
        self.addTab(self.z_score_chart, "z-score")
        self.addTab(self.compare_chart, "% compare chart")
        self.addTab(self.test_tab, "statistics")
        self.addTab(self.backtest_tab, "backtest log")
        self.addTab(self.results_tab, "results")

        self.show_statistical_tests()
        self.show_backtest_log()
        self.show_results()

        self.setCurrentIndex(-1)
        self.button.clicked.connect(self.rerun)
        self.button2.clicked.connect(self.save_results)
        self.pushButton_40.clicked.connect(self.close_analysis)


    def close_analysis(self):
        self.parenttabs.removeTab(self.parenttabs.currentIndex())

    def rerun(self):
        self.change_config()
        self.show_statistical_tests()


    def change_config(self):
        for k, v in self.combos.items():
            self.config[k] = self.options[k][v.currentText()]


    def show_statistical_tests(self):

        df = pd.DataFrame({self.coin1: self.df1['Close'], self.coin2: self.df2['Close']})
        stats = self.zaur.test_for_cointegration(df, self.config['adf_reg'], self.config['kpss_reg'],
                                                 self.config['adf_lag'], self.config['kpss_lag'])

        corr = self.df1['Close'].corr(self.df2['Close']).__round__(3)

        results_table = MyTable(self.groupBox)
        results_table.setGeometry(QtCore.QRect(5, 65, 270, 145))

        df2 = pd.DataFrame(index=['Pearson correlation', 'Hedge ratio', 'R^2', 'Adjusted R^2'], columns=['statistics'], data={'statistics':
                                    [corr, stats[0], stats[1], stats[2]]})

        model1 = PandasModel(df2)
        results_table.setModel(model1)

        tests_table = MyTable(self.groupBox)
        tests_table.setGeometry(QtCore.QRect(5, 220, 700, 85))
        model2 = PandasModel(stats[3])
        tests_table.setModel(model2)
        tests_table.setColumnWidth(0, 200)

        adfs_table = MyTable(self.groupBox)
        adfs_table.setGeometry(QtCore.QRect(5, 320, 190, 85))
        model3 = PandasModel(pd.DataFrame(stats[4]))
        adfs_table.setModel(model3)
        #adfs_table.setColumnWidth(0, 190)




    def show_backtest_log(self):

        self.backtest_table = MyTable(self.backtest_tab)
        self.backtest_table.setGeometry(QtCore.QRect(0, 10, 888, 408))
        self.analysis_df.index = self.analysis_df.index.map(lambda x: ccxt.Exchange.iso8601(x).replace('T', ' ').replace('Z', '')[:-7])
        model = PandasModel(self.analysis_df)
        self.backtest_table.setModel(model)
        self.backtest_table.setSortingEnabled(False)
        self.backtest_table.setHorizontalScrollBar(QtWidgets.QScrollBar())



    def show_results(self):

        self.results_table = MyTable(self.results_frame)
        self.results_table.setGeometry(QtCore.QRect(5, 5, 400, 450))

        self.results_table.setSortingEnabled(False)
        model2 = PandasModel(pd.DataFrame(self.results))
        self.results_table.setModel(model2)
        self.results_table.setColumnWidth(0, 500)


    def save_results(self):
        try:
            results = pd.read_csv('results.csv', index=True)
            results.iloc[len(results)] = self.results
            results.to_csv('results.csv')

        except FileNotFoundError:
            results = pd.DataFrame(columns=[self.results.index.values])
            results.iloc[0] = self.results
            results.to_csv('results.csv')






